import Board from './Board.js'
import React, { Component } from 'react'
import Player from './player'
import InstructionButton from './instructionButton'

class App extends Component {

  constructor(props){
    super(props);
    this.increaseScore = this.increaseScore.bind(this);
    this.state = {

      playable: false,
      gameEnded: false,
      xIsNext: false,
      instruction: 'Click me to Play',
      playerOneScore: 0,
      playerTwoScore: 0
    }
  }

  increaseScore(player){
    console.log(this)
    if(player === "playerOne"){
      this.setState({playerOneScore: this.state.playerOneScore + 1})
    }
    else{this.setState({playerTwoScore: this.state.playerTwoScore + 1})}
  }

  instructionHandler = (gameState) =>{
    switch(gameState){
      case "playerOneTurn":
        this.setState({instruction: "It's Player 1's Turn"})
        break;
      case "playerTwoTurn":
        this.setState({instruction: "It's Player 2's Turn"})
        break;
      case "gameEnded":
        this.setState({gameEnded: true})


    }

  }

  clickHandler(){

  }

  render() {
    return (
      <div className="interface">
        <div className="game">
          <Player playerID = {'Player One'} playerScore = {`Score: ${this.state.playerOneScore}`}/>
          <Board increaseScore={this.increaseScore} playable = {this.state.gameState}/>
          <Player playerID = {'Player Two'} playerScore = {`Score: ${this.state.playerTwoScore}`}/>
        </div>

        <InstructionButton text={this.state.gameState.instruction}/>
      </div>


    )
  }
}




export default App;
